import React,{useEffect, useState} from 'react'
import {useParams,useNavigate, Navigate} from "react-router-dom" 
import axios from 'axios';
function Test() {
    const navigate=useNavigate();
    const {id}=useParams();

    // const info=JSON.parse(localStorage.getItem("test"))
    // console.log(info)

    const [que,setque]=useState([])
    const [data,setdata]=useState(JSON.parse(localStorage.getItem("test"))||[])
    const [question,setquestion]=useState(data.questions[0].questionText);
     const[option,setoption]=useState(data.questions[0].options)
    const [optbutton,setoptbutton]=useState("");
    const [count,setcount]=useState(0)
    const [ans,setans]=useState(Array(data.questions.length).fill(null))
    const [corropt,setcoropt]=useState([])
    const [ansarr,setansarr]=useState("")
    let count1=0;

    useEffect(()=>{
            if(count<data.questions.length)
            {      
              setque(data.questions)
              setquestion(data.questions[count].questionText)
              setoption(data.questions[count].options)
            //console.log("o",option);  
            }
    },[count])

    useEffect(() => {
        if(data.questions[count].type==="Multiple-Response")
        {
            setoptbutton("checkbox")
        }
        else
        {
            setoptbutton("radio")
        }
    },)

    useEffect(() => 
    {
          setans(ansarr)   

    }, [count])


      const setQuestion=()=>
      {
            if(count<data.questions.length-1)
            {            
                setcount(prev=>prev+1)
            }   
      }

      const setQuestion1=()=>
      {
            if(count<data.questions.length && count>0)
            {            
                setcount(prev=>prev-1)

                setque(data.questions)
                setquestion(data.questions[count].questionText)
                setoption(data.questions[count].options)
            }   
      }

    //   console.log(data.questions.length);
    // console.log("s",data);
    //  console.log("se",data.questions[count]._id);


    //   const saveans=(e)=>{
    //     //   console.log("hi");
    //         ans.push(e.target.value)
    //         console.log(ans);
    //         corropt.push(data.questions[count].correctOptionIndex)
    //         // console.log("opt",data.questions[count].correctOptionIndex);
    //     }

        const finish=()=>{
            console.log(corropt);
        }


       const savecheckboxval=(e)=>
        {
            if(e.target.checked)
            {
                let temp=[];
                temp.push(e.target.value)
                // console.log(temp);
                // ansarr.push(temp)
                setansarr([...ansarr, ...temp])
                console.log("ans",ansarr);

            }
            else
            {
                let temp1
               temp1= ansarr.filter((obj)=>obj !== e.target.value)
                setansarr(temp1)      
             }
             count1++;
             console.log(count1);

            // console.log(data.questions[count].options)    
            console.log("ans1",ansarr);
        }

        const saveradioval=(e)=>{

            let temp=parseInt(e.target.value)

            ans[count]=temp
            console.log("ans",ans);


        }


        // console.log("temp",ansarr);   

    return (
    <div>
    <div className="container">
        <div className="row">
        <h1>My Interview Portal</h1>

            <div className="col-md-12">
                <div className="panel panel-default">
                    <div className="panel-heading">{data.name}</div>
                    <div className="panel-body">
                        <form>                
                            <label>[{count+1}]{question} </label>
                            {    
                            option.map((op,index)=>{     
                            const function2=optbutton==="checkbox" ? savecheckboxval : saveradioval  
                            return(     
                            <div className="radio" key={op}>
                                <label>
                                    <input type={optbutton} name="option" value={index} onChange={function2}/> {op}
                                </label>
                            </div>
                            );
                            })    
                        }                  
                        </form>
                    </div>
                    <div className="panel-footer">

                      <button className="btn btn-success" onClick={()=>setQuestion1()}>Previous</button>

                        <button className="btn btn-success" onClick={()=>setQuestion()}>Next</button>


                        <button className="pull-right btn btn-danger" onClick={()=>{finish();navigate("/Finish")}} >Finish</button>
                    </div>
                </div>
            </div>
        </div>
    </div>

        {/* {(que.length==count && count!=0 ) ? complete() : ""} */}

    </div>
  )
}

export default Test
