import React,{useEffect, useState} from 'react'
import {useParams,useNavigate,Link} from "react-router-dom" 
import axios from 'axios';

function Test() {
    const navigate=useNavigate();
     const {id}=useParams();
     console.log("exam",id);
    const[test,settest]=useState([])
    const [question, setQuestion] = useState("");
    const[count,setCount]=useState(0)
    // const[question,setquestion]=useState([])
    // const[examId,setExamId]=useState(JSON.parse(localStorage.getItem("Exam_id")))


    useEffect(() => {
        axios.get("https://dip-kaluse.github.io/examport/portal.json")
        .then(res=>{
            console.log(res.data)
            const result=res.data.tests.filter((obj,index)=>obj._id==id)
            settest(result[0]);

            const ind = result[0].questions.filter((obj, index) => {
                if (obj._id === id)  {
                  setCount(index);
                  return index;
                }
              });
        })
        .catch(err=>{
            console.log("error");
        })
        }, [])

        const handleNextQuestion = () => {
            setCount((prev) => prev + 1);
          };

          const handlePreviosQuestion = () => {
            setCount((prev) => prev - 1);
          };

          const myfun={
            margin:"10px"
          };


        console.log("a",test);
        console.log(test!=null && test.length>0);
        // console.log(test.questions.length);
        return (
    <div>
   <div className="container">
        <div className="row">
            <h1>My Interview Portal</h1>

            <div className="col-md-12">
                <div className="panel panel-default">
                    <div className="panel-heading">{test.length!=0 && test.name}</div>
                    <div className="panel-body">
                        <form>
                            <label>{test.length!=0 && test.questions[count].questionText}</label>
                            <div className="radio">
                                <label>
                                    <input type="radio" name="option" value="option1"/> Javascript Framework
                                </label>
                            </div>
                            <div className="radio">
                                <label>
                                    <input type="radio" name="option" value="option2"/> Java Library
                                </label>
                            </div>
                            <div className="radio">
                                <label>
                                    <input type="radio" name="option" value="option2"/> Programming Language
                                </label>
                            </div>
                        </form>
                    </div>
                    <div className="panel-footer">

                    <Link to={test.length!=0 && (`/Test/${test._id}/${test.questions[count]._id}`)}>    
                    <button className="btn btn-success" style={myfun} disabled={count==0} onClick={()=>{ handlePreviosQuestion()}}>Previous</button>
                    </Link>   


                    <Link to={test.length!=0 && (`/Test/${test._id}/${test.questions[count]._id}`)}>    
                    <button className="btn btn-success" disabled={count==test.question.length} onClick={()=>{ handleNextQuestion()}}>Next</button>
                    </Link>   

                        <a href="finish.html" className="pull-right btn btn-danger">Finish</a>

                    </div>
                </div>
            </div>
        </div>
    </div>

        {/* {(que.length==count && count!=0 ) ? complete() : ""} */}

    </div>
  )
}

export default Test
